import React, { useState ,useEffect} from "react";
import "bootstrap/dist/css/bootstrap.css";
// import { Modal } from "bootstrap";

const Update=()=>{
  const [books,setBooks]=useState([]);
  const [quary,setQuary]=useState("");
  const booktable=async()=>{
    let result=await fetch('http://localhost:5000/getbooks');
    result= await result.json();
    setBooks(result);
  }
   useEffect(()=>{
     booktable();
  },[]); 
console.log("name:",books);

const [IsOpen,setIsOpen] = useState(false);

 return(
    <div> 
       <div className="topnav">
      <h2 id="head">Admin Portal</h2>
      </div>
        <div className="sidebar">
        <a href="Home.js"><i className="fa fa-fw fa-home" id="navtag"></i> Home</a>
        <a href="Admin_dash.js"><i className="fa fa-fw fa-wrench" id="navtag"></i> Add Books</a>
        <a href="Adminsearch.js"><i className="fa fa-fw fa-envelope" id="navtag"></i> Book Search</a>
        <a href="#clients"><i className="fa fa-fw fa-user" id="navtag"></i> Book Update</a>
        <a href="#contact"><i className="fa fa-fw fa-envelope" id="navtag"></i> View Order</a>
        <a href="#contact"><i className="fa fa-fw fa-envelope" id="navtag"></i> Log Out</a>
      </div>
      <div className="backtable">
      <div className="form-group" id="search">
      <form className="d-flex" onSubmit={booktable}>
            {/* <h4>Search Books</h4><br/> */}
              <input className="form-control me-3 p-2" type="text" placeholder="Search Books" onChange={(e)=>setQuary(e.target.value)}/>
              <button className="col btn btn-dark mx-1 " id="sbtn" type="button" >Search</button>
            </form>
          </div>
          <div id="table">
      <table className="table table-bordered"  >
    <thead>
      <tr>
        <th>Book Id</th>
        <th>Title</th>
        <th>Author Name</th>
        <th>Cost</th>
        <th>Book Qantity</th>
      </tr>
    </thead>
    <tbody>
      {books.filter((books)=>
       books.title.toLowerCase().includes(quary) || 
       books.id.toLowerCase().includes(quary) ||
       books.author_name.toLowerCase().includes(quary)
      ).map((item)=>(
           <tr key={item.id}>
            <td>{item.id}</td>
            <td>{item.title}</td>
            <td>{item.author_name}</td>
            <td>{item.cost}</td>
            <td>{item.book_quantity}</td>
            <td><button className="col btn btn-dark mx-1" type="button" onClick={()=>setIsOpen(true)} >
            Update
          </button></td>
           </tr>
         ))
      }      
    </tbody>
    </table>
         </div>
         </div>
         {IsOpen &&(
           <div id="page">
           {/* //{console.log("user",user)} */}
           <form className="bg-primary px-4 py-4" >
             <p className="h2 text-white">Registration Form</p>
             <div className="form-group">
               <label className="my-2">Enter the user name</label>
               <input
                 className="form-control"
                 name="name"

                 required 

               ></input>
             </div>
             <div className="form-group" controlid="formBasicEmail">
               <label className="my-2">Enter the email</label>
               <input
                 className="form-control"
                 name="email"value={email} 

                 required 

                 type ="email"
               ></input>
             </div>
             <div className="form-group">
               <label className="my-2">Enter the phone number</label>
               <input
                 className="form-control"
                 name="mobile"
                 value={mobile}
                 required

               ></input>
             </div>
             <div className="form-group">
               <label className="my-2">Enter the passowrd</label>
               <input
                 className="form-control"
                 name="password"
                 value={password}
                 required

                 minLength='6'
               ></input>
             </div>
             <div className="form-group">
               <label className="my-2">Enter the confirm passowrd</label>
               <input
                 className="form-control"
                 name="cpassword"
                 value={cpassword}
                 required

                 minLength='6'
               ></input>
             </div>
             <div className="row text-center my-3 px-2">
               <button className="col btn btn-dark mx-1" type="submit">
                 Submit
               </button>
               <a
                 className="col btn btn-outline-light mx-1"
                 href="login.js"
                 role="button"
               >

                 Go Login
               </a>
             </div>
           </form></div>
    //      <div className="a" id="a" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true"><br/>
    //            <h3 >Add Books<button type="button" className="btn-close " onClick={()=>setIsOpen(false)}></button></h3>


    //      <form className="bg- px-4 py-4"  >

    //     <div className="form-group">
    //         <input className="form-control" type="number"  placeholder="Book Id"></input><br/>
    //     </div>
    //     <div className="form-group">
    //         <input className="form-control"  placeholder="Book Title"></input><br/>
    //     </div>
    //     <div className="form-group">
    //         <input className="form-control"  placeholder="Author Name"></input><br/>
    //     </div>
    //     <div className="form-group">
    //         <input className="form-control"  placeholder="Book Cost"></input><br/>
    //     </div>
    //     <div className="form-group">
    //         <input className="form-control"  type="number" placeholder="Book Qantity"></input>
    //     </div>

    //     <div className='row text-center my-3 px-2'>
    //         <button className='col btn btn-dark mx-1' type="submit"  onClick={()=>setIsOpen(false)}>Close</button>
    //     </div>
    //  </form>
    //   </div>
      )}
      </div>
      )
}
export default Update;
