import "bootstrap/dist/css/bootstrap.css";
// import e from "express";
import React,{useState} from "react";

function Register() {  

  // const {name,useName}=useState("");
  // const {email,useEmail}=useState("");
  // const {mobile,useMobile}=useState("");
  // const {password,usePassword}=useState("");
  // const {cpassword,useCpassword}=useState("");

  // const handlesubmit=(e)=>{
  //    e.preventDefault();
  //    console.log(name);
  // }
  const[user,setUser]=useState({
    name:"",
    email:"",
    mobile:"",
    password:"",
    cpassword:""
    })
    const handleChange=e =>{
      const {name,value}=e.target
      // console.log(e.target)
      setUser({
        ...user,
        [name]:value
      })

      //console.log(name,value)
    }
    // const handlesubmit=()=>{
    //   const(name,email,password)=user
    // }

  return (
    <div id="page" >
     {console.log("user",user)}
      <form className='bg-primary px-4 py-4' >
        <p className='h2 text-white'>Registration Form</p>
        <div className='form-group'>
            <label className='my-2' >Enter the user name</label>
            <input className='form-control' name="name" value={user.name} required onChange={handleChange} ></input>    
        </div>
        <div className='form-group'>
            <label className='my-2' >Enter the email</label>
            <input className='form-control'name="email" value={user.email} required onChange={handleChange}></input>    
        </div>
        <div className='form-group'>
            <label className='my-2' >Enter the phone number</label>
            <input className='form-control' name="mobile" value={user.mobile} required onChange={handleChange}></input>    
        </div>
        <div className='form-group'>
            <label className='my-2' >Enter the passowrd</label>
            <input className='form-control' name="password"value={user.password}required onChange={handleChange}></input>    
        </div>
        <div className='form-group'>
            <label className='my-2' >Enter the confirm passowrd</label>
            <input className='form-control' name="cpassword" value={user.cpassword} required onChange={handleChange}></input>    
        </div>
        <div className='row text-center my-3 px-2'>
            <button className='col btn btn-dark mx-1' type="submit">Submit</button>
            <a className='col btn btn-outline-light mx-1' href="login.js" role="button"> Go Login</a>
        </div>
      </form>
    </div>
  );
}
export default Register;
