                  <td>
                    <button
                      className="col btn btn-dark mx-1"
                      type="button"
                      onClick={() => {
                        var result = window.confirm(
                          "Do you want to Return the book?.."
                        );
                        if (result) {
                          increment();
                        }
                      }}
                    >
                      Return
                    </button>
                  </td>
                </tr>
              ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};
export default Search;
