 //import { addListener } from "nodemon";
import React, { useState ,useEffect} from "react";

const Search =()=>{
  const [books,setBooks]=useState([]);
  const booktable=async()=>{
    let result=await fetch('http://localhost:5000/getbooks');
    result= await result.json();
    setBooks(result);
  }
  useEffect(()=>{
    booktable();
  },[]);

const [quary,setQuary]=useState("");
//const [IsformOpen,setIsformOpen] = useState(false);

      let [orderdata,setOrderdata]=useState([])
      const getid=async(_id)=>{
        try{
        console.log("id:",_id)
      //   fetch(`http://localhost:5000/getOne/${_id}`,
      //   {
      //     method: 'GET',
      //     headers: {
      //         'Content-Type': 'application/json'
      //     }
      // })

      // .then(console.log("getdata"))
        let orderdata=await fetch(`http://localhost:5000/getOne/${_id}`);
        orderdata=await orderdata.json();
        setOrderdata(orderdata);
        console.log("data:",orderdata)
        console.log("last data :",setOrderdata)
        }catch(err){ console.log(err)};
      }

      const [quantity,setQuantity]=useState([])
      const [count,setCount]=useState();
const increment=()=>{
   const orderbook = orderdata.book_quantity-1;
  setCount(orderdata.book_quantity-1)
  // setCount(orderdata.map((item,index)=>item.book_quantity === key=?
  // {...item,orderdata: item.book_quantity-1} : item))
  console.log("orderbook : ",orderbook)
}
const logout=()=>{
  localStorage.clear();
  window.location.href='/Home';
}

 return(
    <div>
        <div className="topnav">
      <h2 id="head">User Portal</h2>
      </div>
        <div className="sidebar">
        <a href="Home"><i className="fa fa-fw fa-home" id="navtag"></i> Home</a>

        <a href="#services"><i className="fa fa-fw fa-wrench" id="navtag"></i> Book Search</a>
        <a href="/place_order"><i className="fa fa-fw fa-envelope" id="navtag"></i> Book Details</a>
        <a href="#" onClick={logout}><i className="fa fa-fw fa-envelope" id="navtag"></i> Log Out</a>
      </div>
      <div className="form-group" id="search">
      <form className="d-flex">
            {/* <h4>Search Books</h4><br/> */}
              <input className="form-control me-3 p-2" type="text" placeholder="Search Books" onChange={(e)=>setQuary(e.target.value)}/>
              <button className="col btn btn-dark mx-1 " id="sbtn" type="button">Search</button>
            </form>
          </div>
          <div id="table">
      <table className="table table-bordered"  >
    <thead>
      <tr>
        <th>Book Id</th>
        <th>Title</th>
        <th>Author Name</th>
        <th>Cost</th>
        <th>Book Qantity</th>
      </tr>
    </thead>
    <tbody>
      {books.filter((books)=>
       books.title.toLowerCase().includes(quary) || 
       books.id.toLowerCase().includes(quary) ||
       books.author_name.toLowerCase().includes(quary)
      ).map((item)=>(
           <tr key={item.id}>
            <td>{item.id}</td>
            <td>{item.title}</td>
            <td>{item.author_name}</td>
            <td>{item.cost}</td>
            <td value={orderdata.book_quantity} onChange={(e)=>{setQuantity(e.target.value)}}>{item.book_quantity}{increment}</td>
            <td><button className="col btn btn-dark mx-1" type="button"
            onClick={()=>{
              var result=window.confirm("Do you want to order the book?..");
              getid(item._id);
              if(result){

                // setIsformOpen(true);

                 increment();
                }
            }}>Order</button> </td>
            <td><button className="col btn btn-dark mx-1" type="button" 
            onClick={()=>{
              var result=window.confirm("Do you want to Return the book?..");
              if(result){
                     increment()
              }
            }}
            >Return</button></td>
           </tr>
         ))

      }      
    </tbody>
    </table>
         </div>
      </div>
      )
}
export default Search;
