import "bootstrap/dist/css/bootstrap.css";
import React, { useState,useEffect } from "react";
// import Popup from 'reactjs-popup';
// import 'reactjs-popup/dist/index.css';

function Place_order() {
    const [books,setBooks]=useState([]);
    const booktable=async()=>{
      let result=await fetch('http://localhost:5000/getbooks');
      result= await result.json();
      setBooks(result);
    }
    console.log(books);
    useEffect(()=>{
        booktable();
      },[]);
    //open the form
      const [IsformOpen,setIsformOpen] = useState(false);

      const [orderdata,setOrderdata]=useState([])
      const getid=async(_id)=>{
        try{
        console.log("id:",_id)
        let orderdata=await fetch(`http://localhost:5000/getOne/${_id}`);
        // {
        //     method: 'GET',
        //     headers: {
        //         'Content-Type': 'application/json'
        //     }
        // })
        orderdata=await orderdata.json();
        setOrderdata(orderdata);
        console.log("data:",orderdata)
        }catch(err){ console.log(err)};
        // .then(console.log("updated"))
        // .catch(err => console.log(err));
      }

  return(
          <div>
            <div className="topnav">
          <h2 id="head">User Portal</h2>
          </div>
            <div className="sidebar">
            <a href="Home"><i className="fa fa-fw fa-home" id="navtag"></i> Home</a>
            <a href="Search"><i className="fa fa-fw fa-wrench" id="navtag"></i> Book Search</a>
            <a href="#contact"><i className="fa fa-fw fa-envelope" id="navtag"></i> View Date Info</a>
            <a href="#clients"><i className="fa fa-fw fa-user" id="navtag"></i> Return Book</a>
            <a href="#contact"><i className="fa fa-fw fa-envelope" id="navtag"></i> Place Order</a>
            <a href="#contact"><i className="fa fa-fw fa-envelope" id="navtag"></i> Log Out</a>
          </div>
          <div id="table">
          <table className="table table-bordered">
        <thead>
          <tr>
            <th>Book Id</th>
            <th>Title</th>
            <th>Author Name</th>
            <th>Cost</th>
            <th>Book Qantity</th>
          </tr>
        </thead>
        <tbody>
          {
            books.map((item,_id)=>(
               <tr key={item._id}>
                <td>{item.id}</td>
                <td>{item.title}</td>
                <td>{item.author_name}</td>
                <td>{item.cost}</td>
                <td>{item.book_quantity}</td>
                <td><button className="col btn btn-dark mx-1" type="button"   
                onClick={()=>{ 
                  var result=window.confirm("Do you want to order the book?..");
                  if(result){
                  setIsformOpen(true);
                  getid(item._id);
                  }
                  }}
                > Order
          </button></td>
               </tr>
            ))
          }
        </tbody>
        </table>
             </div>
             {IsformOpen &&(
             <div id="order">

                  <div>
                    <p ></p>
                <p className="text-white">{gettitle}</p>
                <p className="text-white">{author_name}</p>
                 </div>

              <button id="ordbutton" className="col btn btn-dark mx-1" 
             onClick={()=>setIsformOpen(false)} type="button" >close</button>
             </div>
             )} 

   </div>
        );

}
export default Place_order;
