// import "bootstrap/dist/css/bootstrap.css";
// import React, { useState } from "react";

import { useState ,userEffect} from "react";

function User() {
  const {books,setBooks}=useState([]);

  const booktable=(e)=>{
   e.preventDefault();
   fetch("http://localhost:5000/getbooks", {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify(books),
  }).then((res) => {
    console(json => setBooks(json));
   // console.log("post data: ",books);
    console.log("books", res);
    alert("Books are added successfully..!")
  })
  }
  userEffect(()=>{
    booktable();
  },[]);

    return(
      <div>
        <div className="topnav">
      <h2 id="head">User Portal</h2>
      </div>
        <div class="sidebar">
        <a href="Home.js"><i class="fa fa-fw fa-home" id="navtag"></i> Home</a>
        <a href="#services"><i class="fa fa-fw fa-wrench" id="navtag"></i> Book Search</a>
        <a href="#contact"><i class="fa fa-fw fa-envelope" id="navtag"></i> View Date Info</a>
        <a href="#clients"><i class="fa fa-fw fa-user" id="navtag"></i> Return Book</a>
        <a href="#contact"><i class="fa fa-fw fa-envelope" id="navtag"></i> Please Order</a>
        <a href="#contact"><i class="fa fa-fw fa-envelope" id="navtag"></i> Log Out</a>
      </div>
      <div id="table">
      <table class="table table-bordered">
    <thead>
      <tr>
        <th>Firstname</th>
        <th>Lastname</th>
        <th>Email</th>
      </tr>
    </thead>
    <tbody>
      {
        books.map((item)=>(
           <tr key={item.id}>
            <td>{item.id}</td>
            <td>{item.title}</td>
            <td>{item.author_name}</td>
            <td>{item.cost}</td>
            <td>{item.book_quantity}</td>
           </tr>
        ))
      }
    </tbody>
    </table>
         </div>
      </div>
    );
}
export default User;
